import React, { useEffect, useReducer } from 'react';
import { useParams } from 'react-router-dom';
import Search from '../../components/Search/Search';
import Tags from '../../components/Tags/Tags';
import Thumbnails from '../../components/Thumbnails/Thumbnails';
import {
  getAll,
  getAllByTag,
  getAllTags,
  search,
} from '../../services/foodService';
import NotFound from '../../components/NotFound/NotFound';

const initialState = {
  foods: [],
  tags: [],
  error: null,
};

const reducer = (state, action) => {
  switch (action.type) {
    case 'FOODS_LOADED':
      return { ...state, foods: action.payload, error: null };
    case 'TAGS_LOADED':
      return { ...state, tags: action.payload };
    case 'ERROR':
      return { ...state, error: action.payload };
    default:
      return state;
  }
};

export default function HomePage() {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { foods, tags, error } = state;
  const { searchTerm, tag } = useParams();

  useEffect(() => {
    const loadTags = async () => {
      try {
        const tags = await getAllTags();
        dispatch({ type: 'TAGS_LOADED', payload: tags });
      } catch (err) {
        dispatch({ type: 'ERROR', payload: 'Failed to load tags' });
      }
    };

    const loadFoods = async () => {
      try {
        const foods = tag
          ? await getAllByTag(tag)
          : searchTerm
          ? await search(searchTerm)
          : await getAll();
        dispatch({ type: 'FOODS_LOADED', payload: foods });
      } catch (err) {
        dispatch({ type: 'ERROR', payload: 'Failed to load foods' });
      }
    };

    loadTags();
    loadFoods();
  }, [searchTerm, tag]);

  return (
    <>
      <Search />
      <Tags tags={tags} />
      {error && <div>{error}</div>}
      {foods.length === 0 && !error ? (
        <NotFound linkText="Reset Search" />
      ) : (
        <Thumbnails foods={foods} />
      )}
    </>
  );
}
